#  Combien y a-t-il de handle dans cette array ?

def handle(name_twitter_journalists)
  puts name_twitter_journalists.count
end

#  Quel est le handle le plus court de cette liste ?
def little(name_twitter_journalists)
  min = name_twitter_journalists[0]
  name_twitter_journalists.each { |name|
    if name.length < min.length
      min = name
    end
  }

  puts min
end


#  Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
def h_with_cinq(name_twitter_journalists)
  five_size = []
  name_twitter_journalists.map { |name_five|
    if (name_five.length) - 1 == 5
      five_size << name_five
    end
  }

  puts five_size.count
end


#  Combien commencent par une majuscule (première lettre juste après le @) ?
def first_maj(name_twitter_journalists)
  first_letter = []
  name_twitter_journalists.map { |e|
    if e[1] =~ /[A-Z]/
      first_letter << e
    end
  }

  puts first_letter.count
end

#  Trie la liste de handle par ordre alphabétique.

def alph(name_twitter_journalists)
  puts  alpha = name_twitter_journalists.sort
end


#  Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)

def size_len(name_twitter_journalists)
  puts name_twitter_journalists.sort_by(&:length)
end
#  Quelle est la position dans l'array de la personne @epenser ?

def epenser(name_twitter_journalists)
  puts  name_twitter_journalists.index("@epenser")
end

#  Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)
def size_handles(name_twitter_journalists)
  min = "@min"
  s = []
  handle_size = min.length
  name_twitter_journalists.sort_by(&:length).map { |h|
    if handle_size == h.length
      s << h
    end
    if handle_size < h.length
      puts "Journalistes de #{handle_size} caractères: #{s.length}"
      s = []
      handle_size = h.length
      s << h
    end
    if name_twitter_journalists.sort_by(&:length).last == h
      puts "Journalistes de #{handle_size} caractères: #{s.length}"
    end
  }
end

